mapper.select:
select seq, fk_userid, name, subject, readCount, regDate, commentCount, groupno, fk_seq, depthno , filename from ( select rownum AS rno, seq, fk_userid, name, subject, readCount, regDate, commentCount, groupno, fk_seq, depthno , filename from ( select seq, fk_userid, name, subject, readCount, to_char(regDate, 'yyyy-mm-dd hh24:mi:ss') AS regDate, commentCount, groupno, fk_seq, depthno , filename from tbl_board where status = 1
and lower (${searchType}) like '%' || lower( #{searchWord} ) || '%'
start with fk_seq = 0 connect by prior seq = fk_seq order siblings by groupno desc, seq desc ) V ) T where rno between #{startRno} AND #{endRno}

    	select ceil(count(*)/ to_number( #{sizePerPage} ) ) 
		from tbl_comment
		where status = 1 and parentSeq = #{parentSeq}

select previousseq , previoussubject , seq , fk_userid , name , subject , content , readcount , regdate , pw , nextseq , nextsubject , groupno , fk_seq , depthno , fileName , orgFilename , fileSize from ( select lag(seq, 1) over(order by seq desc ) as previousseq, lag(subject, 1) over(order by seq desc ) as previoussubject, seq , fk_userid , name , subject , content , readcount , to_char(regdate , 'yyyy-mm-dd hh24:mi:ss' ) as regdate , pw , lead ( seq , 1 ) over ( order by seq desc ) as nextseq , lead ( subject , 1 ) over ( order by seq desc ) as nextsubject , groupno , fk_seq , depthno , fileName , orgFilename , fileSize from tbl_board where status = 1
and lower (${searchType}) like '%' || lower(#{searchWord}) || '%'
) V where V.seq = #{seq}

mapper.update:
update tbl_reservation set mailSendCheck = 1 
 where reservationSeq in 

   ${arr_reservationSeq[i]}
