mapper.select:
select count(*)
from tbl_calendar_small_category
where fk_lgcatgono = 2 and smcatgoname = #{com_smcatgoname}

select count(*)
from tbl_calendar_small_category
where fk_lgcatgono = 1 and fk_userid = #{fk_userid} and smcatgoname = #{my_smcatgoname}

mapper.update:
update tbl_calendar_schedule set startdate = to_date(#{startdate}, 'yyyymmddhh24miss')
                               , enddate = to_date(#{enddate}, 'yyyymmddhh24miss')
                               , subject = #{subject}
                               , fk_lgcatgono = #{fk_lgcatgono}
                               , fk_smcatgono = #{fk_smcatgono}
                               , color = #{color}
                               , place = #{place}
                               , joinuser = #{joinuser}
                               , content = #{content}
where scheduleno = #{scheduleno}

mapper.select:
    select count(*)
	from tbl_calendar_small_category
	where fk_lgcatgono = #{caltype} 
	and fk_userid = #{userid} 
	and smcatgoname = #{smcatgoname}

select scheduleno , startdate, enddate , smcatgoname, lgcatgoname, name , subject, content from ( select row_number() over(order by SD.scheduleno desc) as rno , SD.scheduleno , to_char(SD.startdate, 'yyyy-mm-dd hh24:mi') as startdate , to_char(SD.enddate, 'yyyy-mm-dd hh24:mi') as enddate , SC.smcatgoname, LC.lgcatgoname, M.name , SD.subject, SD.content from tbl_calendar_schedule SD JOIN tbl_member M ON SD.fk_userid = M.userid JOIN tbl_calendar_small_category SC ON SD.fk_smcatgono = SC.smcatgono JOIN tbl_calendar_large_category LC ON SD.fk_lgcatgono = LC.lgcatgono
where ( to_char(SD.startdate,'YYYY-MM-DD') between #{startdate} and #{enddate} ) AND ( to_char(SD.enddate,'YYYY-MM-DD') between #{startdate} and #{enddate} )
and ( SD.fk_lgcatgono = 2 OR SD.fk_userid = #{fk_userid} OR ( SD.fk_userid != #{fk_userid} and lower(SD.joinuser) like '%'||lower(#{fk_userid})||'%' ) )
and ( SD.fk_lgcatgono = 2 )
and ( SD.fk_lgcatgono = 1 AND SD.fk_userid = #{fk_userid} )
and SD.fk_lgcatgono = 1 and SD.fk_userid = #{fk_userid} and lower(SD.joinuser) like '%'||lower(#{searchWord})||'%'
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
and SD.fk_lgcatgono = 1 and SD.fk_userid = #{fk_userid}
and SD.fk_lgcatgono = 2
) V where V.rno between #{startRno} and #{endRno}
